  const Header = (props) => {
    return (
      <div>
        <p>
          <h1>{props.nimi}</h1>
        </p>
      </div>
    )
  }

  //content osat ja tehtävämäärä
  const Content = (props) => {
    return (
      <div>
        <p>
          {props.part1} {props.paljonkoFundamentaaleja}
        </p>
        <p>
          {props.part2} {props.paljonkoPropsesja}
        </p>
        <p>
          {props.part3} {props.paljonkoKomponentteja}
        </p>
      </div>   
    )
  }


